		},
		// 画线方法
		drawLine({ x, y }) {
			if (this.tx_list_activate != '画笔' && this.tx_type_activate != '橡皮') return;
			// #ifdef MP-WEIXIN
			if (this.tx_type_activate != '橡皮') {
				this.setCanvasStyle();
				// 绘制圆形让用户看到路径
				this.ctx.beginPath();
				this.ctx.arc(x, y, this.pen_size/2, 0, 2 * Math.PI);
				this.ctx.fill();
				this.ctx.draw(true);
			}
			// #endif
			// #ifndef MP-WEIXIN
			this.setCanvasStyle();
			// 绘制圆形让用户看到路径
			this.ctx.beginPath();
			this.ctx.arc(x, y, this.pen_size/2, 0, 2 * Math.PI);
			this.ctx.fill();
			this.ctx.draw(true);
			// #endif
		},
		drawLineHX({ x, y }) {
			if (this.tx_list_activate != '画笔' || this.tx_type_activate == '橡皮') return;
			this.setCanvasStyle();
			// 在回显canvas上绘制圆形
			this.ctx_hx.beginPath();
			this.ctx_hx.arc(x, y, this.pen_size/2, 0, 2 * Math.PI);
			this.ctx_hx.fill();
			this.ctx_hx.draw(true);
		},
		// 画矩形
		drawRect({ x, y }) {
			if (this.tx_list_activate != '矩形' || this.tx_type_activate == '橡皮') return;
			let width, heigth;
			let dx, dy;
			if (this.moveTo.x > x) {
				dx = x;
				width = this.moveTo.x - x;
			} else {
				dx = this.moveTo.x;
				width = x - this.moveTo.x;
			}
			if (this.moveTo.y > y) {
				dy = y;
				heigth = this.moveTo.y - y;
			} else {
				dy = this.moveTo.y;
				heigth = y - this.moveTo.y;
			}
			this.setCanvasStyle();
			this.ctx.rect(dx, dy, width, heigth);
			this.ctx.stroke();
			this.ctx.draw(true);
			this.$emit('getRectPosition', { x1: dx, y1: dy, x2: width + dx, y2: heigth + dy });
		},
		// 回显画矩形
		drawRectHX({ x, y }) {
			if (this.tx_list_activate != '矩形' || this.tx_type_activate == '橡皮') return;
			let width, heigth;
			let dx, dy;
			if (this.moveTo.x > x) {
				dx = x;
				width = this.moveTo.x - x;
			} else {
				dx = this.moveTo.x;
				width = x - this.moveTo.x;
			}
			if (this.moveTo.y > y) {
				dy = y;
				heigth = this.moveTo.y - y;
			} else {
				dy = this.moveTo.y;
				heigth = y - this.moveTo.y;
			}
